import './App.css';

function App() {
  const [ number, setNumber ] = useState(0);

   useEffect(() => {
     setInterval(() => {
       const newNumber = Math.round(Math.random() * 100);
       setNumber(newNumber)
     }, 10000)
  }, [number])

  return (
    <div className="App">
      <header className="App-header">
        { number % 3 === 0 || number % 5 === 0 ? 
        <p>ACERTOU!!!</p>
        : number }
      </header>
    </div>
  );
}

export default App;
