import './App.css';

const NEW_NUMBER = Math.round(Math.random() * 100);

function App() {
  const [ number, setNumber ] = useState(0);

   useEffect(() => {
     const intervalId = setInterval(() => {
       setNumber(NEW_NUMBER)
     }, 10000)
  }, [number])

  return (
    <div className="App">
      <header className="App-header">
        { number }
      </header>
    </div>
  );
}

export default App;
